import { MsgConnectionOpenAck } from '@cosmjs-types/src/ibc/core/connection/v1/tx';

class MsgConnectionOpenAckMockBuilder {
  private msg: MsgConnectionOpenAck;

  constructor() {
    this.setDefault();
  }

  private setDefault(): void {
    this.msg = {
      connection_id: 'connection-4',
      counterparty_connection_id: 'connection-35',
      version: { identifier: '1', features: ['ORDER_ORDERED', 'ORDER_UNORDERED'] },
      client_state: {
        type_url: '/ibc.clients.cardano.v1.ClientState',
        value: Buffer.from([]),
      },
      proof_height: { revision_number: 0n, revision_height: 224576n },
      proof_try: Buffer.from(
        '',
        'hex',
      ),
      proof_client: Buffer.from(
        '',
        'hex',
      ),
      proof_consensus: Buffer.from(
        '',
        'hex',
      ),
      consensus_height: { revision_number: 0n, revision_height: 40779n },
      host_consensus_state_proof: Buffer.from([]),
      signer: 'addr_test1vqj82u9chf7uwf0flum7jatms9ytf4dpyk2cakkzl4zp0wqgsqnql',
    };
  }

  reset(): void {
    this.setDefault();
  }

  withConnectionId(connectionId: string): MsgConnectionOpenAckMockBuilder {
    this.msg.connection_id = connectionId;
    return this;
  }
  withProofHeight(revisionNumber: bigint, revisionHeight: bigint): MsgConnectionOpenAckMockBuilder {
    this.msg.proof_height = {
      revision_height: revisionHeight,
      revision_number: revisionNumber,
    };
    return this;
  }
  withCounterpartyConnectionId(counterpartyConnectionId: string): MsgConnectionOpenAckMockBuilder {
    this.msg.counterparty_connection_id = counterpartyConnectionId;
    return this;
  }

  withVersionIdentifier(identifier: string): MsgConnectionOpenAckMockBuilder {
    this.msg.version.identifier = identifier;
    return this;
  }

  withVersionFeatures(features: string[]): MsgConnectionOpenAckMockBuilder {
    this.msg.version.features = features;
    return this;
  }

  withClientStateTypeUrl(typeUrl: string): MsgConnectionOpenAckMockBuilder {
    this.msg.client_state.type_url = typeUrl;
    return this;
  }

  withClientStateValue(value: Buffer): MsgConnectionOpenAckMockBuilder {
    this.msg.client_state.value = value;
    return this;
  }

  withProofTry(proofTry: Buffer): MsgConnectionOpenAckMockBuilder {
    this.msg.proof_try = proofTry;
    return this;
  }

  withProofClient(proofClient: Buffer): MsgConnectionOpenAckMockBuilder {
    this.msg.proof_client = proofClient;
    return this;
  }

  withProofConsensus(proofConsensus: Buffer): MsgConnectionOpenAckMockBuilder {
    this.msg.proof_consensus = proofConsensus;
    return this;
  }

  withSigner(signer: string): MsgConnectionOpenAckMockBuilder {
    this.msg.signer = signer;
    return this;
  }

  build(): any {
    const builtMsg = { ...this.msg };
    this.reset();
    return builtMsg;
  }
}

const msgConnectionOpenAckMockBuilder = new MsgConnectionOpenAckMockBuilder();

export default msgConnectionOpenAckMockBuilder;
