import "C"
import (
	"encoding/hex"
	"fmt"
	"os"
	"time"
	"unsafe"

	"github.com/fxamacker/cbor/v2"
)

func main() {
	testVerifyBlock()
}

func testVerifyBlock() {
	dt := time.Now()
	fmt.Println("Current date and time is: ", dt.String())

	type BlockHexCbor struct {
		_             struct{} `cbor:",toarray"`
		Flag          int
		HeaderCbor    string
		Eta0          string
		Spk           int
		BlockBodyCbor string
	}

	// 1 tx
	// https://cardanoscan.io/block/9688517

	// block := BlockHexCbor{
	// 	HeaderCbor:    "",
	// 	Eta0:          "909a297951d129b42fe98ef08ae0b60e4da2c9e69a080ae028197f988243bb7f",
	// 	Spk:           129600,
	// 	BlockBodyCbor: "",
	// }

	// 0 tx
	// https://cardanoscan.io/block/9734693

	// block := BlockHexCbor{
	// 	HeaderCbor:    "",
	// 	Eta0:          "4d66f1cd8274bb44ad073d07b1cc6a8c99bdea25f8e031f2470716357c449438",
	// 	Spk:           129600,
	// 	BlockBodyCbor: "80",
	// }

	// 10 tx
	// https://cardanoscan.io/block/9734701

	block := BlockHexCbor{
		HeaderCbor:    "",
		Eta0:          "B92E64A5E1DD902815B6F9F030BC250049F9B1AA40346E483E7067118F8617CA",
		Spk:           129600,
		BlockBodyCbor: "",
	}

	blockCbor, err := cbor.Marshal(block)
	if err != nil {
		fmt.Println("error:", err)
	}
	blockCborHex := hex.EncodeToString(blockCbor)
	fmt.Println("blockCborHex:", blockCborHex)
	///////////////////////
	argv := os.Args
	argc := C.int(len(argv))
	c_argv := (*[0xfff]*C.char)(C.allocArgv(argc))
	defer C.free(unsafe.Pointer(c_argv))

	for i, arg := range argv {
		c_argv[i] = C.CString(arg)
		defer C.free(unsafe.Pointer(c_argv[i]))
	}
	// debug output of the args.
	// C.printArgs(argc, (**C.char)(unsafe.Pointer(c_argv)))

	// calling Haskell.
	C.init_hs(argc, (**C.char)(unsafe.Pointer(c_argv)))
	defer C.hs_exit()
	///////////////////////

	blockCborHexC := C.CString(blockCborHex)
	defer C.free(unsafe.Pointer(blockCborHexC))

	verifyBlockOutputCString := C.verifyBlock_hs(blockCborHexC)
	verifyBlockOutput := C.GoString(verifyBlockOutputCString)
	defer C.free(unsafe.Pointer(verifyBlockOutputCString))

	type UTXOOutputToken struct {
		_              struct{} `cbor:",toarray"`
		Flag           int
		TokenAssetName string
		TokenValue     string
	}
	type UTXOOutput struct {
		_           struct{} `cbor:",toarray"`
		Flag        int
		TxHash      string
		OutputIndex string
		Tokens      []UTXOOutputToken
		DatumHex    string
	}

	type RegisCert struct {
		_            struct{} `cbor:",toarray"`
		Flag         int
		RegisPoolId  string
		RegisPoolVrf string
	}

	type DeRegisCert struct {
		_             struct{} `cbor:",toarray"`
		Flag          int
		DeRegisPoolId string
		DeRegisEpoch  string
	}

	type VerifyBlockOutput struct {
		_             struct{} `cbor:",toarray"`
		Flag          int
		IsValid       bool
		VrfKHexString string
		Outputs       []UTXOOutput
		RegisCerts    []RegisCert
		DeRegisCerts  []DeRegisCert
	}
	data, _ := hex.DecodeString(verifyBlockOutput)
	var vOutput VerifyBlockOutput
	err2 := cbor.Unmarshal(data, &vOutput)
	if err2 != nil {
		fmt.Println("error:", err)
	}
	fmt.Println("verifyBlockOutput:", vOutput)
	isValid := vOutput.IsValid
	if isValid == true {
		fmt.Println("Valid")
	} else {
		fmt.Println("Not valid")
	}

	dtx := time.Now()
	fmt.Println("Total run time: ", (dtx.Sub(dt)).String())
}

func testVerifyBlockHeader() {
	dt := time.Now()
	fmt.Println("Current date and time is: ", dt.String())
	argv := os.Args
	argc := C.int(len(argv))
	c_argv := (*[0xfff]*C.char)(C.allocArgv(argc))
	defer C.free(unsafe.Pointer(c_argv))

	for i, arg := range argv {
		c_argv[i] = C.CString(arg)
		defer C.free(unsafe.Pointer(c_argv[i]))
	}
	// debug output of the args.
	// C.printArgs(argc, (**C.char)(unsafe.Pointer(c_argv)))

	// calling Haskell.
	C.init_hs(argc, (**C.char)(unsafe.Pointer(c_argv)))
	defer C.hs_exit()
	// C.basicPotato()
	headerHex := C.CString("")
	defer C.free(unsafe.Pointer(headerHex))
	eta0Hex := C.CString("411148a40d6c2b49dea994da90fcca559418c63ac8fff8e0d708a8799a10f790")
	defer C.free(unsafe.Pointer(eta0Hex))
	slotsPerKESPeriod := 129600
	isValid := C.verifyHeaderPraos_hs(headerHex, C.int(slotsPerKESPeriod), eta0Hex)
	fmt.Println("isValid:", isValid)
	if isValid == 1 {
		fmt.Println("Valid")
	} else {
		fmt.Println("Not valid")
	}
	dtx := time.Now()
	fmt.Println("Total run time: ", (dtx.Sub(dt)).String())
}
